#include <iostream>

using namespace std;

void merge(int *a, int *b, int low, int pivot, int high, int &contador) {
    int h,i,j,k;
    h=low;
    i=low;
    j=pivot+1;

    while((h<=pivot)&&(j<=high))
    {
        if(a[h]<=a[j])
        {
            b[i]=a[h];
            h++;
        }
        else
        {
            b[i]=a[j];
            j++;
        }
        i++;
    }
    if(h>pivot)
    {
        for(k=j; k<=high; k++)
        {
            b[i]=a[k];
            i++;
        }
    }
    else
    {
        for(k=h; k<=pivot; k++)
        {
            b[i]=a[k];
            i++;
        }
    }
    for(k=low; k<=high; k++) a[k]=b[k];
    contador++;
}

void mergesort(int *a, int*b, int low, int high, int &contador)
{
    int pivot;
    if(low<high) {
        int p1 = (high-low)/3;
        int p2 = 2*(high-low)/3;          
        mergesort(a,b,low,low+p1, contador);
        mergesort(a,b,low+p1+1,low+p2, contador);
        mergesort(a,b,low+p2+1,high, contador);
        merge(a,b,low,low+p1,low+p2, contador);
        merge(a,b,low,low+p2,high, contador);
    }
}

